                        $(this).find('.h2_eqivalent').hide();
                      } else {
                        $.each($(this).find('ul'), function() {
                          var liPresent = $(this).children().length;
                          if (liPresent > 0) {
                            var txt = $("li", this).text();
                            if (txt.length <= 0) {
                              $(this).hide();
                              if (!ulExits) {
                                $(this).siblings('.h2_eqivalent').hide();
                              }
                            } else {
                              ulExits = true;
                              $(this).show();
                              $(this).siblings('.h2_eqivalent').show();
                            }
                          } else {
                            $(this).hide();
                            if (!ulExits) {
                              $(this).siblings('.h2_eqivalent').hide();
                            }
                          }
                        });
                      }
                    });
